	public boolean insertNew(String goodsName,String placeName,String specification,String sellPrice,String buyPrice){
		
		boolean result=false;
		ApplicationContext context = new ClassPathXmlApplicationContext(
				"applicationContext.xml");
		IDataConfig myData = (IDataConfig) context.getBean("dataConfigProxy");
		myData.init();
		try {
			myData.openConnection();
			myData.beginTransaction();
			String sql1 ="insert into goods (goodsName,specification,placeName,buyPrice,sellPrice) values ('" + goodsName + "','" + specification + "','" + placeName + "','" + buyPrice + "','" + sellPrice + "')";
			myData.addBatch(sql1);

			
			if(myData.commit()){
				result=true;
			}else{
				result=false;
			}
			
			myData.endTransaction();
			
		} catch (SQLException e) {
			e.printStackTrace();
		}finally{
			myData.closeConnection();
		}
		return result;
	}
	
